<?phpglobal $listingpro_options;$banner_height = isset($listingpro_options['banner_height2']) && !empty($listingpro_options['banner_height2']) ? $listingpro_options['banner_height2'] : 610;$top_title     = $listingpro_options['top_main_title'];$main_text_des = $listingpro_options['main_text'];$banner_logo   = $listingpro_options['banner_logo_search2'];$banner_forgroundimage   =  $listingpro_options['home_banner_forground']['url'];$search_pos = $listingpro_options['home_banner_search_position'];$locationType  = 'withip';?><div class="lp-header-search-wrap lp-header-search-wrap-sidebar-banner md-header-search-wrap pos-relative" style=" height: <?php echo $banner_height; ?>px;">    <?php if(!empty($banner_forgroundimage)): ?>        <img src="<?php echo $banner_forgroundimage; ?>" <?php if ($search_pos == 'bottom'){ echo 'style="bottom:-40px;"'; } ?> class="mp-banner-forground-image" alt="Banner For Ground Image."/>    <?php endif; ?>    <div class="lp-header-search lp-header-search-sidebar-style">        <div class="container">            <div class="row">                <div class="col-md-7">                    <?php                    if (is_front_page()):                                                $args = array(                            'post_type'      => 'listing',                            'post_status'    => 'publish',                            'posts_per_page' => 10,                            'fields'         => 'ids',                            'meta_key'       => 'listing_rate',                            'orderby'        => 'meta_value_num',                            'order'          => 'DESC',                        );                        $doctors = new WP_Query($args);                        $thumbnail_ids = array();                        if($doctors->have_posts()){                            while($doctors->have_posts()): $doctors->the_post();                                $doctor_id = get_the_ID();                                $thumbnail_id = get_post_thumbnail_id($doctor_id);                                if( $thumbnail_id == '' || get_post_status($thumbnail_id) != 'publish' ){                                    $gallery_image_ids = get_post_meta($doctor_id, 'gallery_image_ids', true);                                    if(isset($gallery_image_ids) && !empty($gallery_image_ids) && is_array($gallery_image_ids)){                                        foreach($gallery_image_ids as $gallery_image_id){                                            if(get_post_status($thumbnail_id) == 'publish'){                                                $thumbnail_ids[] = $gallery_image_id;                                                break;                                            }                                        }                                    }                                }else{                                    $thumbnail_ids[get_the_ID()] = $thumbnail_id;                                }                                if(isset($thumbnail_ids) && count($thumbnail_ids) >= 3) break;                            endwhile;                        }                        $found_doctors   = $doctors->found_posts;                        wp_reset_query(); ?>                            <div class="col-md-12 lp_auto_loc_container">                                <div class="lp-header-search-tagline lp-header-search-tagline-sidebar-banner" data-locnmethod="<?php echo esc_attr($locationType); ?>"><?php echo $top_title; ?></div>                                <div class="sidebar-banner-des" data-locnmethod="<?php echo esc_attr($locationType); ?>"><?php echo $main_text_des; ?></div>                                <div class="md-available-docts">                                    <p>                                        <?php                                         if(isset($thumbnail_ids) && !empty($thumbnail_ids)){                                            foreach($thumbnail_ids as $listingID => $thumbnail_id){                                                $image = wp_get_attachment_image_src($thumbnail_id, 'lp-sidebar-thumb-v2');                                                $image = isset($image[0]) ? $image[0] : '';                                                if ($image == '') continue;                                                echo '<span><a href="'.get_permalink($listingID).'"><img src="'. $image .'" alt=""></a></span> ';                                            }                                        }                                        $bannerCountText = isset($listingpro_options['home_banner_post-count']) ? $listingpro_options['home_banner_post-count'] : '';                                        $home_banner_tagline = isset($listingpro_options['home_banner_tagline']) ? $listingpro_options['home_banner_tagline'] : '';                                        printf($bannerCountText,  $found_doctors);                                        ?>                                    </p>                                </div>                                <div class="mp-profile-content-features">                                    <div class="row">                                        <div class="col-md-4">                                            <div class="mp-profile-content-feature-icon">                                                <img src="<?php echo MP_PLUGIN_DIR . 'assets/images/temp/feature1.svg'; ?>" alt="">                                            </div>                                            <span class="mp-profile-content-feature-text"><?php esc_html_e('Video Consultation', 'medicalpro'); ?></span>                                        </div>                                        <div class="col-md-4 padding-0">                                            <div class="mp-profile-content-feature-icon">                                                <img src="<?php echo MP_PLUGIN_DIR . 'assets/images/temp/feature2.svg'; ?>" alt="">                                            </div>                                            <span class="mp-profile-content-feature-text"><?php esc_html_e('Certified Doctors', 'medicalpro'); ?></span>                                        </div>                                        <div class="col-md-4 padding-0">                                            <div class="mp-profile-content-feature-icon">                                                <img src="<?php echo MP_PLUGIN_DIR . 'assets/images/temp/feature3.svg'; ?>" alt="">                                            </div>                                            <span class="mp-profile-content-feature-text"><?php esc_html_e('Online Prescription', 'medicalpro'); ?></span>                                        </div>                                    </div>                                </div>                                <div class="md-sidebar-search-location">                                    <img src="<?php echo MP_PLUGIN_DIR . 'assets/images/icons/Trophy.png'; ?>"> <?php echo $home_banner_tagline; ?>                                </div>                            </div>                    <?php endif; ?>                </div>                <?php                if ($search_pos == 'right'){                    get_template_part('templates/headers/sidesearch');                   }                ?>            </div>        </div>    </div></div><div class="mp-bottom-search-style"><?php    if ($search_pos == 'bottom'){       include( MP_PLUGIN_PATH . "templates/headers/mp-bottom-search.php");       }        ?></div>